import 'dotenv/config';
import express, { type Request, Response, NextFunction } from "express";
import cookieParser from "cookie-parser";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));
app.use(cookieParser());

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";
import { setCsrfToken } from "./csrf";

// Extend the session interface to include custom properties
declare module "express-session" {
  interface SessionData {
    returnTo?: string;
  }
}

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: process.env.NODE_ENV === 'production',
      sameSite: 'strict',
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, (err: any, user: any) => {
      if (err) {
        return next(err);
      }
      if (!user) {
        return res.redirect("/api/login");
      }

      req.logIn(user, (err) => {
        if (err) {
          return next(err);
        }

        // Set CSRF token immediately after successful authentication
        setCsrfToken(res);

        // Redirect to the homepage or return URL
        const returnTo = req.session?.returnTo || "/";
        delete req.session?.returnTo;
        res.redirect(returnTo);
      });
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { insertJournalEntrySchema, insertStreakSchema } from "@shared/schema";
import { csrfTokenProvider, csrfProtection } from "./csrf";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // CSRF middleware
  app.use(csrfTokenProvider);
  app.use(csrfProtection);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // CSRF token endpoint - ensures CSRF token is available for authenticated users
  app.get('/api/auth/csrf', isAuthenticated, async (req: any, res) => {
    try {
      // The csrfTokenProvider middleware will automatically set the token if not present
      // This endpoint just confirms the token is set and returns success
      res.json({ message: "CSRF token set" });
    } catch (error) {
      console.error("Error setting CSRF token:", error);
      res.status(500).json({ message: "Failed to set CSRF token" });
    }
  });

  // Streak routes
  app.get('/api/streak', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const streak = await storage.getStreakByUserId(userId);
      res.json(streak || { currentStreak: 0, longestStreak: 0 });
    } catch (error) {
      console.error("Error fetching streak:", error);
      res.status(500).json({ message: "Failed to fetch streak" });
    }
  });

  app.put('/api/streak', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const streakData = insertStreakSchema.parse(req.body);
      const updatedStreak = await storage.updateStreak(userId, streakData.currentStreak!, streakData.longestStreak!);
      res.json(updatedStreak);
    } catch (error) {
      console.error("Error updating streak:", error);
      res.status(500).json({ message: "Failed to update streak" });
    }
  });

  app.post('/api/streak/reset', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const resetStreak = await storage.resetStreak(userId);
      res.json(resetStreak);
    } catch (error) {
      console.error("Error resetting streak:", error);
      res.status(500).json({ message: "Failed to reset streak" });
    }
  });

  // Journal routes
  app.get('/api/journal', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const entries = await storage.getJournalEntries(userId);
      res.json(entries);
    } catch (error) {
      console.error("Error fetching journal entries:", error);
      res.status(500).json({ message: "Failed to fetch journal entries" });
    }
  });

  app.post('/api/journal', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const entryData = insertJournalEntrySchema.parse(req.body);
      const newEntry = await storage.createJournalEntry(userId, entryData);
      res.json(newEntry);
    } catch (error) {
      console.error("Error creating journal entry:", error);
      res.status(500).json({ message: "Failed to create journal entry" });
    }
  });

  // Milestone routes
  app.get('/api/milestones', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const milestones = await storage.getMilestones(userId);
      res.json(milestones);
    } catch (error) {
      console.error("Error fetching milestones:", error);
      res.status(500).json({ message: "Failed to fetch milestones" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  streaks,
  journalEntries,
  milestones,
  type User,
  type UpsertUser,
  type Streak,
  type JournalEntry,
  type Milestone,
  type InsertJournalEntry,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc } from "drizzle-orm";
import { encryptJournalContent, decryptJournalContent } from "./crypto";

// Interface for storage operations
export interface IStorage {
  // User operations - (IMPORTANT) these user operations are mandatory for Replit Auth.
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Streak operations
  getStreakByUserId(userId: string): Promise<Streak | undefined>;
  updateStreak(userId: string, currentStreak: number, longestStreak: number): Promise<Streak>;
  resetStreak(userId: string): Promise<Streak>;

  // Journal operations
  getJournalEntries(userId: string): Promise<JournalEntry[]>;
  createJournalEntry(userId: string, entry: InsertJournalEntry): Promise<JournalEntry>;

  // Milestone operations
  getMilestones(userId: string): Promise<Milestone[]>;
  createDefaultMilestones(userId: string): Promise<void>;
  updateMilestone(milestoneId: string, isAchieved: boolean, achievedDate?: string): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  // User operations - (IMPORTANT) these user operations are mandatory for Replit Auth.
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();

    // Create default streak and milestones for new users
    await this.ensureUserDefaults(user.id);

    return user;
  }

  // Streak operations
  async getStreakByUserId(userId: string): Promise<Streak | undefined> {
    const [streak] = await db.select().from(streaks).where(eq(streaks.userId, userId));
    return streak;
  }

  async updateStreak(userId: string, currentStreak: number, longestStreak: number): Promise<Streak> {
    const existingStreak = await this.getStreakByUserId(userId);

    if (existingStreak) {
      const [updatedStreak] = await db
        .update(streaks)
        .set({
          currentStreak,
          longestStreak: Math.max(longestStreak, currentStreak),
          updatedAt: new Date(),
        })
        .where(eq(streaks.userId, userId))
        .returning();
      return updatedStreak;
    } else {
      const [newStreak] = await db
        .insert(streaks)
        .values({
          userId,
          currentStreak,
          longestStreak: Math.max(longestStreak, currentStreak),
        })
        .returning();
      return newStreak;
    }
  }

  async resetStreak(userId: string): Promise<Streak> {
    const [resetStreak] = await db
      .update(streaks)
      .set({
        currentStreak: 0,
        lastResetDate: new Date().toISOString().split('T')[0],
        updatedAt: new Date(),
      })
      .where(eq(streaks.userId, userId))
      .returning();
    return resetStreak;
  }

  // Journal operations
  async getJournalEntries(userId: string): Promise<JournalEntry[]> {
    const encryptedEntries = await db
      .select()
      .from(journalEntries)
      .where(eq(journalEntries.userId, userId))
      .orderBy(desc(journalEntries.createdAt));

    // Decrypt journal content before returning with proper error handling
    return encryptedEntries.map(entry => {
      try {
        return {
          ...entry,
          content: decryptJournalContent(entry.content)
        };
      } catch (error) {
        console.error(`Failed to decrypt journal entry ${entry.id}:`, error);
        // Return entry with error message instead of throwing
        return {
          ...entry,
          content: '[ENCRYPTED DATA - DECRYPTION FAILED]'
        };
      }
    });
  }

  async createJournalEntry(userId: string, entry: InsertJournalEntry): Promise<JournalEntry> {
    try {
      // Encrypt the journal content before storing
      const encryptedContent = encryptJournalContent(entry.content);

      const [newEntry] = await db
        .insert(journalEntries)
        .values({
          userId,
          ...entry,
          content: encryptedContent,
        })
        .returning();

      // Return with decrypted content for immediate use
      return {
        ...newEntry,
        content: entry.content // Return original content, not encrypted
      };
    } catch (error) {
      console.error('Failed to create journal entry:', error);
      if (error instanceof Error && error.message.includes('encrypt')) {
        throw new Error('Failed to securely store journal entry - encryption error');
      }
      throw error; // Re-throw other database errors
    }
  }

  // Milestone operations
  async getMilestones(userId: string): Promise<Milestone[]> {
    return await db
      .select()
      .from(milestones)
      .where(eq(milestones.userId, userId))
      .orderBy(milestones.targetDays);
  }

  async createDefaultMilestones(userId: string): Promise<void> {
    const defaultMilestones = [
      { title: "First Day Clean", targetDays: 1 },
      { title: "One Week Strong", targetDays: 7 },
      { title: "Two Weeks Free", targetDays: 14 },
      { title: "One Month Milestone", targetDays: 30 },
      { title: "Two Months Clean", targetDays: 60 },
      { title: "Three Months Strong", targetDays: 90 },
      { title: "Half Year Achievement", targetDays: 180 },
      { title: "One Year Victory", targetDays: 365 },
    ];

    await db.insert(milestones).values(
      defaultMilestones.map(milestone => ({
        userId,
        ...milestone,
      }))
    );
  }

  async updateMilestone(milestoneId: string, isAchieved: boolean, achievedDate?: string): Promise<void> {
    await db
      .update(milestones)
      .set({
        isAchieved,
        achievedDate: achievedDate || null,
      })
      .where(eq(milestones.id, milestoneId));
  }

  // Helper method to ensure user has default data
  private async ensureUserDefaults(userId: string): Promise<void> {
    // Check if user already has streak
    const existingStreak = await this.getStreakByUserId(userId);
    if (!existingStreak) {
      await db.insert(streaks).values({ userId });
    }

    // Check if user already has milestones
    const existingMilestones = await this.getMilestones(userId);
    if (existingMilestones.length === 0) {
      await this.createDefaultMilestones(userId);
    }
  }
}

export const storage = new DatabaseStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

