  --chart-5: 25 95% 53%;

  --font-sans: Inter, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: .75rem; /* 12px */
  --shadow-2xs: 0px 1px 2px 0px hsl(220 15% 22% / 0.05);
  --shadow-xs: 0px 1px 3px 0px hsl(220 15% 22% / 0.08);
  --shadow-sm: 0px 1px 2px 0px hsl(220 15% 22% / 0.05), 0px 2px 4px -1px hsl(220 15% 22% / 0.08);
  --shadow: 0px 1px 3px 0px hsl(220 15% 22% / 0.08), 0px 4px 8px -2px hsl(220 15% 22% / 0.12);
